import React, { useState, useEffect } from 'react';
import './App.css';

function App() {

  const [users, setUsers] = useState([]);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [age, setAge] = useState('');

  useEffect(() => {
    buscarUsuarios();
  }, []);

  function buscarUsuarios() {
    fetch('/api/users')
      .then(response => response.json())
      .then(data => setUsers(data))
      .catch(error => console.log('Erro:', error));
  }

  function adicionarUsuario() {

    if (!name || !email || !age) {
      alert('Por favor, preencha todos os campos!');
      return;
    }

    const novoUsuario = {
      name: name,
      email: email,
      age: age
    };

    fetch('/api/users', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(novoUsuario)
    })
    .then(() => {
      alert('Usu치rio adicionado com sucesso!');
      limparFormulario();
      buscarUsuarios();
    })
    .catch(error => {
      console.log('Erro:', error);
      alert('Erro ao adicionar usu치rio!');
    });
  }

  function deletarUsuario(id) {
    if (window.confirm('Tem certeza que deseja deletar este usu치rio?')) {
      fetch('/api/users/' + id, {
        method: 'DELETE'
      })
      .then(() => {
        alert('Usu치rio deletado com sucesso!');
        buscarUsuarios();
      })
      .catch(error => {
        console.log('Erro:', error);
        alert('Erro ao deletar usu치rio!');
      });
    }
  }

  function limparFormulario() {
    setName('');
    setEmail('');
    setAge('');
  }

  return (
    <div className="container">
      <h1>游닇 Sistema de Usu치rios</h1>

      <div className="form-section">
        <h2>Adicionar Novo Usu치rio</h2>

        <div className="form-group">
          <label>Nome:</label>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder="Digite o nome"
          />
        </div>

        <div className="form-group">
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Digite o email"
          />
        </div>

        <div className="form-group">
          <label>Idade:</label>
          <input
            type="number"
            value={age}
            onChange={(e) => setAge(e.target.value)}
            placeholder="Digite a idade"
          />
        </div>

        <div className="buttons">
          <button onClick={adicionarUsuario} className="btn-add">
             Adicionar Usu치rio
          </button>
          <button onClick={limparFormulario} className="btn-clear">
             Limpar
          </button>
        </div>
      </div>

      <div className="users-section">
        <h2>游논 Lista de Usu치rios ({users.length})</h2>

        {users.length === 0 ? (
          <p className="no-users">Nenhum usu치rio cadastrado ainda.</p>
        ) : (
          <div className="users-grid">
            {users.map(user => (
              <div key={user.id} className="user-card">
                <h3>{user.name}</h3>
                <p> {user.email}</p>
                <p> {user.age} anos</p>
                <button 
                  onClick={() => deletarUsuario(user.id)}
                  className="btn-delete"
                >
                   Deletar
                </button>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
