import React, { useEffect, useState } from 'react';

// Endereço da API do backend
const API_URL = 'http://localhost:3000/api/users';

function App() {
  // Lista de usuários
  const [users, setUsers] = useState([]);
  // Dados do formulário
  const [form, setForm] = useState({ name: '', email: '', age: '' });
  // ID do usuário que está sendo editado
  const [editingId, setEditingId] = useState(null);

  // Carrega os usuários ao abrir a página
  useEffect(() => {
    fetchUsers();
  }, []);

  // Buscar todos os usuários
  function fetchUsers() {
    fetch(API_URL)
      .then(res => res.json())
      .then(data => setUsers(data));
  }

  // Atualiza o formulário quando o usuário digita
  function handleChange(e) {
    setForm({ ...form, [e.target.name]: e.target.value });
  }

  // Envia o formulário para criar ou atualizar usuário
  function handleSubmit(e) {
    e.preventDefault();
    if (editingId) {
      // Atualizar usuário
      fetch(API_URL + '/' + editingId, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(form)
      }).then(fetchUsers);
    } else {
      // Criar novo usuário
      fetch(API_URL, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(form)
      }).then(fetchUsers);
    }
    setForm({ name: '', email: '', age: '' });
    setEditingId(null);
  }

  // Preenche o formulário para editar
  function handleEdit(user) {
    setForm({ name: user.name, email: user.email, age: user.age });
    setEditingId(user.id);
  }

  // Exclui um usuário
  function handleDelete(id) {
    fetch(API_URL + '/' + id, { method: 'DELETE' })
      .then(fetchUsers);
  }

  return (
    <div>
      <h2>Usuários</h2>
      <form onSubmit={handleSubmit}>
        <input
          name="name"
          placeholder="Nome"
          value={form.name}
          onChange={handleChange}
          required
        />
        <input
          name="email"
          placeholder="Email"
          value={form.email}
          onChange={handleChange}
          required
        />
        <input
          name="age"
          placeholder="Idade"
          value={form.age}
          onChange={handleChange}
          required
          type="number"
        />
        <button type="submit">{editingId ? 'Atualizar' : 'Adicionar'}</button>
        {editingId && (
          <button type="button" onClick={() => { setEditingId(null); setForm({ name: '', email: '', age: '' }); }}>
            Cancelar
          </button>
        )}
      </form>
      <table border="1">
        <thead>
          <tr>
            <th>Nome</th>
            <th>Email</th>
            <th>Idade</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {users.map(user => (
            <tr key={user.id}>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>{user.age}</td>
              <td>
                <button onClick={() => handleEdit(user)}>Editar</button>
                <button onClick={() => handleDelete(user.id)}>Excluir</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
