import React, { useState, useEffect } from 'react';

const API = 'http://localhost:3000/api/users';

function App() {
  const [users, setUsers] = useState([]);
  const [form, setForm] = useState({ name: '', email: '', age: '' });
  const [id, setId] = useState();

  useEffect(() => { fetch(API).then(r => r.json()).then(setUsers); }, []);

  function handleChange(e) {
    setForm({ ...form, [e.target.name]: e.target.value });
  }

  function handleSubmit(e) {
    e.preventDefault();
    const method = id ? 'PUT' : 'POST';
    const url = id ? API + '/' + id : API;
    fetch(url, {
      method,
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(form)
    }).then(() => {
      fetch(API).then(r => r.json()).then(setUsers);
      setForm({ name: '', email: '', age: '' });
      setId();
    });
  }

  function edit(u) {
    setForm({ name: u.name, email: u.email, age: u.age });
    setId(u.id);
  }

  function del(id) {
    fetch(API + '/' + id, { method: 'DELETE' })
      .then(() => fetch(API).then(r => r.json()).then(setUsers));
  }

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input name="name" value={form.name} onChange={handleChange} placeholder="Nome" required />
        <input name="email" value={form.email} onChange={handleChange} placeholder="Email" required />
        <input name="age" value={form.age} onChange={handleChange} placeholder="Idade" required type="number" />
        <button type="submit">{id ? 'Atualizar' : 'Adicionar'}</button>
        {id && <button type="button" onClick={() => { setForm({ name: '', email: '', age: '' }); setId(); }}>Cancelar</button>}
      </form>
      <table border="1">
        <thead>
          <tr><th>Nome</th><th>Email</th><th>Idade</th><th></th></tr>
        </thead>
        <tbody>
          {users.map(u => (
            <tr key={u.id}>
              <td>{u.name}</td>
              <td>{u.email}</td>
              <td>{u.age}</td>
              <td>
                <button onClick={() => edit(u)}>Editar</button>
                <button onClick={() => del(u.id)}>Excluir</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
